import { Link, useNavigate } from 'react-router-dom'
const getStatusText = (status) => {
  switch (status) {
    case 0:
      return 'Open'
    case 1:
      return 'In Progress'
    case 2:
      return 'Closed'
    default:
      return 'Unknown'
  }
}

function TicketItem({ ticket }) {  
  const navigate = useNavigate();
  const userRole = localStorage.getItem('role');
  const handleChangeStatus = (ticketId) => {
    navigate(`/change-status/${ticketId}`);
  };
  return (
    <div className='ticket'>
      <div>{new Date(ticket.createdAt).toLocaleString('en-US')}</div>
      <div>{ticket.name}</div>
      <div className={`status status-${ticket.status}`}>
        <Link to={`/change-status/${ticket._id}`}>{getStatusText(ticket.status)}</Link>
      </div>
      {userRole === 'admin' && ticket.status === 0 && !ticket.assignedTo ? (
        <Link to={`/assign-ticket/${ticket._id}`} className='btn btn btn-sm'>Assign Ticket</Link>
      ) : userRole === 'agent' ? (
        <div>{ticket.assignedTo ? ticket.assignedTo.name : 'Unassigned'}</div>
      ) : null}
      <Link to={`/ticket/${ticket._id}`} className='btn btn-reverse btn-sm'>
        View
      </Link>
    </div>
  )
}

export default TicketItem
