import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { BackButton } from '../components/BackButton';
import Spinner from '../components/Spinner';
import TicketItem from '../components/TicketItem';
import { assignTicket, getTickets, reset } from '../features/tickets/ticketSlice';

// Helper function to get the status text from the status number
function Tickets() {
  const { tickets, isLoading, isSuccess, user } = useSelector(
    (state) => state.tickets
  )
  const dispatch = useDispatch()

  useEffect(() => {
    return () => {
      if (isSuccess) {
        dispatch(reset())
      }
    }
  }, [dispatch, isSuccess])

  useEffect(() => {
    dispatch(getTickets())
  }, [dispatch])

  const handleAssign = (ticketId) => {
    dispatch(assignTicket({ ticketId, userId: user._id }));
  };

  if (isLoading) {
    return <Spinner />
  }

  return (
    <>
      <BackButton url="/" />
      <h1>Tickets</h1>
      <div className="tickets">
        <div className="ticket-headings">
          <div>Date</div>
          <div>Ticket Name</div>
          <div>Status</div>
          <div>Assigned To</div>
          <div></div>
        </div>
        {tickets.length > 0 ? (
          tickets.map((ticket) => (
            <div key={ticket._id} className="ticket-item">
              <TicketItem ticket={ticket} />
              {ticket.status === 0 && !ticket.assignedTo ? (
                <button onClick={() => handleAssign(ticket._id)}>Assign Ticket</button>
              ) : (
                <div>{ticket.assignedTo ? ticket.assignedTo.name : 'Unassigned'}</div>
              )}
            </div>
          ))
        ) : (
          <p>No tickets found</p>
        )}
      </div>
    </>
  );
}

export default Tickets;
