import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { BackButton } from '../components/BackButton';
import Spinner from '../components/Spinner';
import TicketItem from '../components/TicketItem';
import { getAgentTickets, reset } from '../features/tickets/ticketSlice';

// Helper function to get the status text from the status number
function Tickets() {
  const { tickets, isLoading, isSuccess } = useSelector(
    (state) => state.tickets
  )
  const dispatch = useDispatch()

  useEffect(() => {
    return () => {
      if (isSuccess) {
        dispatch(reset())
      }
    }
  }, [dispatch, isSuccess])

  useEffect(() => {
    dispatch(getAgentTickets())
  }, [dispatch])

  const handleStatusChange = (ticketId, newStatus) => {
    // Dispatch action to update ticket status
    // dispatch(updateTicketStatus({ ticketId, newStatus }))
  }

  if (isLoading) {
    return <Spinner />
  }

  const filteredTickets = tickets.filter(ticket => 
    ticket.status === '0' || ticket.status === '1'
  );

  return (
    <>
      <BackButton url="/" />
      <h1>My Assigned Tickets</h1>
      <div className="tickets">
        <div className="ticket-headings">
          <div>Date</div>
          <div>Ticket Name</div>
          <div>Status</div>
          <div>Change status</div>
        </div>
        {filteredTickets.length > 0 ? (
          filteredTickets.map((ticket) => (
            <div key={ticket._id} className="ticket-item">
              <TicketItem ticket={ticket} />
              <select
                value={ticket.status}
                onChange={(e) => handleStatusChange(ticket._id, e.target.value)}
              >
                <option value="open">Open</option>
                <option value="in progress">In Progress</option>
                <option value="closed">Closed</option>
              </select>
            </div>
          ))
        ) : (
          <p>No tickets found</p>
        )}
      </div>
    </>
  );
}

export default Tickets;
