import axios from 'axios'
const API_URL = '/auth/'

// Register user
const register = async (userData) => {
  const response = await axios.post(API_URL+ 'register', userData)
  console.log('from register', response)
  if (response.data) {
    localStorage.setItem('user', JSON.stringify(response.data))
  }
  return response.data
}

// Login user
const login = async (userData) => {
  const response = await axios.post(API_URL + 'login', userData)
  console.log('from register', response)
  if (response.data) {
    localStorage.setItem('user', JSON.stringify(response.data))
  }
  return response.data
}

// Logout user
const logout = () => localStorage.removeItem('user')

// Get users
const getUsers = async () => {
  const response = await axios.get("/users/all")
  return response.data
}

// Delete user
const deleteUser = async (userId) => {
  const response = await axios.delete(`/users/id/${userId}`)
  return response.data
}

// Verify registration
const verifyRegister = async (token) => {
  const response = await axios.post(API_URL + 'verify-register', { token })
  return response.data
}

// Verify email
const verifyEmail = async (token) => {
  const response = await axios.post(API_URL + 'verify-email', { token })
  return response.data
}

const authService = {
  register,
  logout,
  login,
  getUsers,
  deleteUser,
  verifyRegister,
  verifyEmail, // add verifyEmail to authService
}

export default authService
