import axios from 'axios';
import { useEffect, useState } from 'react';
import { useNavigate, useParams, Link } from 'react-router-dom';

function UpdateStatus() {
  const { ticketId } = useParams();
  const navigate = useNavigate();
  const [status, setStatus] = useState(null);
  const [ticket, setTicket] = useState(null);

  useEffect(() => {
    const fetchTicket = async () => {
      try {
        const response = await axios.get(`/tickets/${ticketId}`);
        setTicket(response.data);
        setStatus(response.data.status);
      } catch (error) {
        console.error('Error fetching ticket:', error);
      }
    };
    fetchTicket();
  }, [ticketId]);

  const handleStatusChange = async (newStatus) => {
    try {
      await axios.put(`/tickets/${ticketId}/status`, { status: newStatus });
      setStatus(newStatus);
      navigate(`/ticket/${ticketId}`);
    } catch (error) {
      console.error('Error updating status:', error);
    }
  };

  if (!ticket) return <div>Loading...</div>;

  return (
    <div className="update-status-container">
      <h2>Update Status for Ticket: {ticket.name}</h2>
      <div className="form-group-table">
        <div className="form-group-row">
          <div className="form-group-cell">
            <input
              type="radio"
              id="in-progress"
              value="1"
              checked={status === 1}
              onChange={() => handleStatusChange(1)}
            />
            <label htmlFor="in-progress">In Progress</label>
          </div>
        </div>
        <div className="form-group-row">
          <div className="form-group-cell">
            <input
              type="radio"
              id="closed"
              value="2"
              checked={status === 2}
              onChange={() => handleStatusChange(2)}
            />
            <label htmlFor="closed">Closed</label>
          </div>
        </div>
      </div>
          </div>
  );
}

export default UpdateStatus;
