import { Link , useNavigate} from 'react-router-dom'
import { useSelector } from 'react-redux'
const getStatusText = (status) => {
  switch (status) {
    case 0:
      return 'Open'
    case 1:
      return 'In Progress'
    case 2:
      return 'Closed'
    default:
      return 'Unknown'
  }
}



function TicketItem({ ticket }) {  
  const { user } = useSelector((state) => state.auth)
  const navigate = useNavigate();
  const handleChangeStatus = (ticketId) => {
    navigate(`/change-status/${ticketId}`);
  };
  return (
    <div className='ticket'>
      <div>{new Date(ticket.createdAt).toLocaleString('en-US')}</div>
      <div>{ticket.name}</div>

      <div className={`status status-${ticket.status}`}>{getStatusText(ticket.status)}</div>
      {ticket.status === 0 && !ticket.assignedTo ? (
        <Link to={`/assign-ticket/${ticket._id}`} className='btn btn btn-sm'>Assign Ticket</Link>
      ) : (
        <div>{ticket.assignedTo ? ticket.assignedTo.name : 'Unassigned'}</div>
      )}
      <button onClick={() => handleChangeStatus(ticket._id)}>Change Status</button>
      <Link to={`/ticket/${ticket._id}`} className='btn btn-reverse btn-sm'>
        View
      </Link>

    </div>
  )
}

export default TicketItem
