import { Link, useNavigate } from 'react-router-dom'

const getStatusText = (status) => {
  switch (status) {
    case 0:
      return 'Open'
    case 1:
      return 'In Progress'
    case 2:
      return 'Closed'
    default:
      return 'Unknown'
  }
}

function TicketItem({ ticket }) {
  const navigate = useNavigate();
  const handleAssign = (ticketId) => {
    if (ticketId) {
      navigate(`/assign-ticket/${ticketId}`);
    } else {
      console.error('Ticket ID is undefined');
    }
  };

  return (
    <div className='ticket'>
      <div>{new Date(ticket.createdAt).toLocaleString('en-US')}</div>
      <div>{ticket.name}</div>
      <div className={`status status-${ticket.status}`}>{getStatusText(ticket.status)}</div>
      {ticket.status === 0 && !ticket.assignedTo ? (
        <Link onClick={() => handleAssign(ticket._id)} className='btn btn btn-sm'>Assign Ticket</Link>
      ) : (
        <div>{ticket.assignedTo ? ticket.assignedTo.name : 'Unassigned'}</div>
      )}

      <Link to={`/ticket/${ticket._id}`} className='btn btn-reverse btn-sm'>
        View
      </Link>

    </div>
  )
}

export default TicketItem
